CREATE TABLE IF NOT EXISTS aliass(id INTEGER PRIMARY KEY AUTOINCREMENT,alias TEXT NOT NULL,type TEXT NOT NULL);
CREATE TABLE IF NOT EXISTS OnlineAccount(account_id INTEGER PRIMARY KEY AUTOINCREMENT,username TEXT NOT NULL,password TEXT NOT NULL,id INTEGER, FOREIGN KEY (id) REFERENCES Alias (id)  ON UPDATE CASCADE ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS CreditOrDebitCard(card_id INTEGER PRIMARY KEY AUTOINCREMENT,cardNumber INTEGER NOT NULL,cvvNumber INTEGER NOT NULL,pinNumber INTEGER NOT NULL,expDate INTEGER NOT NULL,id INTEGER, FOREIGN KEY (id) REFERENCES Alias (id)  ON UPDATE CASCADE ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS sharedWith(sEmail INTEGER PRIMARY KEY, sName TEXT NOT NULL, username TEXT, password TEXT, cardNumber INTEGER, cvvNumber INTEGER, pinNumber INTEGER, id INTEGER, FOREIGN KEY (id) REFERENCES Alias (id)  ON UPDATE CASCADE ON DELETE CASCADE);